import React, { useState } from "react";

const AddDataModal = ({ isOpen, onClose, onAddData }) => {
    const [newData, setNewData] = useState({ name: "", email: "", phone: "" });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = () => {
        onAddData(newData); // Pass the new data to the parent
        setNewData({ name: "", email: "", phone: "" }); // Reset form
    };

    if (!isOpen) return null; // Don't render modal if not open

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>데이터 추가</h3>
                <label>Name: <input type="text" name="name" value={newData.name} onChange={handleChange} /></label>
                <label>Email: <input type="email" name="email" value={newData.email} onChange={handleChange} /></label>
                <label>Phone: <input type="text" name="phone" value={newData.phone} onChange={handleChange} /></label>
                <button onClick={handleSubmit}>추가</button>
            </div>
        </div>
    );
};

export default AddDataModal;
