// src/components/Pages/ShowList.js
import React, { useState } from 'react';
import DataList from './DataList';
import AddModal from '../AddModal';
import UpdateModal from '../UpdateModal';

function ShowList() {
    const [isAddOpen, setAddOpen] = useState(false);
    const [isUpdateOpen, setUpdateOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);

    const handleAddData = async (data) => {
        await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data),
        });
    };

    const handleUpdateData = async (data) => {
        await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${data.id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data),
        });
    };

    const openUpdateModal = (item) => {
        setSelectedItem(item);
        setUpdateOpen(true);
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현</h1>
            <button onClick={() => setAddOpen(true)}>데이터 추가</button>
            <DataList onEdit={openUpdateModal} />
            <AddModal isOpen={isAddOpen} onClose={() => setAddOpen(false)} onAdd={handleAddData} />
            <UpdateModal isOpen={isUpdateOpen} onClose={() => setUpdateOpen(false)} onUpdate={handleUpdateData} item={selectedItem} />
        </div>
    );
}

export default ShowList;
