import React, { useState, useEffect, useRef } from "react";

function UpdateDataForm({ item, onUpdateData, onClose }) {
  const [updatedData, setUpdatedData] = useState({
    name: item.name,
    email: item.email,
    phone: item.phone,
  });
  const [updateCount, setUpdateCount] = useState(0);

  // Refs for validation
  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);

  useEffect(() => {
    // This effect ensures the update count is set to 0 whenever the page loads
    setUpdateCount(0);
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({ ...prev, [name]: value }));

    // API call to update data immediately as input changes
    updateDataAPI(name, value);
  };

  const updateDataAPI = async (field, value) => {
    try {
      const updatedItem = { ...updatedData, [field]: value };
      const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${item.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedItem),
      });
      if (response.ok) {
        setUpdateCount((prev) => prev + 1); // Increment the update count
      } else {
        alert("수정 실패");
      }
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleBlur = (e) => {
    // Validate onBlur for each input field
    const { name } = e.target;
    if (name === "name" && nameRef.current && nameRef.current.value === "") {
      alert("이름을 입력해 주세요.");
    }
    if (name === "email" && emailRef.current && emailRef.current.value === "") {
      alert("이메일을 입력해 주세요.");
    }
    if (name === "phone" && phoneRef.current && phoneRef.current.value === "") {
      alert("전화번호를 입력해 주세요.");
    }
  };

  return (
    <form>
      <label>
        ID: <input type="text" name="id" value={item.id} readOnly />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={updatedData.name}
          onChange={handleChange}
          onBlur={handleBlur}
          ref={nameRef}
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={updatedData.email}
          onChange={handleChange}
          onBlur={handleBlur}
          ref={emailRef}
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={updatedData.phone}
          onChange={handleChange}
          onBlur={handleBlur}
          ref={phoneRef}
        />
      </label><br />
      <div>수정 횟수: {updateCount}</div>
      <button type="button" onClick={onClose}>닫기</button>
    </form>
  );
}

export default UpdateDataForm;
