import React, { useState } from "react";
import DataList from "../DataList";
import AddDataModal from "../AddDataModal";
import UpdateDataModal from "../UpdateDataModal";

const ShowList = () => {
    const [data, setData] = useState([]);
    const [isDataLoaded, setIsDataLoaded] = useState(false); // 데이터를 조회했는지 여부

    // Fetch data from the server
    const fetchData = async () => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
            const result = await response.json();
            setData(result);
            setIsDataLoaded(true); // 데이터가 로드되었음을 설정
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    // Add data and refresh list
    const handleAddData = async (newData) => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newData),
            });
            if (response.ok) {
                alert("데이터가 추가되었습니다!");
                fetchData(); // 새로 추가된 데이터를 즉시 불러옴
            }
        } catch (error) {
            console.error("Error adding data:", error);
        }
    };

    // Update data and refresh list
    const handleUpdateData = async (id, updatedData) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updatedData),
            });
            if (response.ok) {
                alert("데이터가 수정되었습니다!");
                fetchData(); // 수정된 데이터를 즉시 불러옴
            }
        } catch (error) {
            console.error("Error updating data:", error);
        }
    };

    // Delete data and refresh list
    const handleDeleteData = async (id) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                alert("데이터가 삭제되었습니다!");
                fetchData(); // 삭제 후 데이터 목록을 갱신
            }
        } catch (error) {
            console.error("Error deleting data:", error);
        }
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
            <button onClick={fetchData}>데이터 조회</button>
            <button onClick={() => setIsAddModalOpen(true)}>데이터 추가</button>

            <AddDataModal onAddData={handleAddData} />
            {isDataLoaded && (
                <DataList data={data} onUpdateData={handleUpdateData} onDeleteData={handleDeleteData} />
            )}
        </div>
    );
};

export default ShowList;
