import React, { useState } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  // 데이터 변경 시 상태 업데이트
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  // 유효성 검사 후 데이터 추가
  const handleSubmit = async () => {
    if (!newData.id || !newData.name || !newData.email || !newData.phone) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    try {
      const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      if (response.ok) {
        onAddData(); // 데이터 갱신
        alert("데이터가 추가되었습니다.");
      } else {
        alert("데이터 추가 실패");
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }

    onClose(); // 모달 닫기
  };

  // 폼 유효성 검사 및 제출
  const handleChangeAndSubmit = (e) => {
    handleChange(e);
    handleSubmit(); // 데이터 변경 시 자동으로 제출
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm onChange={handleChangeAndSubmit} newData={newData} />
      </div>
    </div>
  );
}

export default AddDataModal;
