import React, { useState, useRef } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  // 각 input에 대한 ref 설정
  const idRef = useRef(null);
  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);

  // 데이터 변경 시 상태 업데이트
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  // 유효성 검사 및 데이터 추가
  const handleSubmit = async () => {
    // useRef로 유효성 검사
    if (!idRef.current.value || !nameRef.current.value || !emailRef.current.value || !phoneRef.current.value) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    // 데이터 추가 요청
    try {
      const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      if (response.ok) {
        onAddData(); // 데이터 갱신
        alert("데이터가 추가되었습니다.");
      } else {
        alert("데이터 추가 실패");
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }

    onClose(); // 모달 닫기
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm
          onChange={handleChange}
          newData={newData}
          idRef={idRef}
          nameRef={nameRef}
          emailRef={emailRef}
          phoneRef={phoneRef}
        />
        <button onClick={handleSubmit}>추가</button>
      </div>
    </div>
  );
}

export default AddDataModal;
