import React, { useState, useRef } from "react";

function AddDataForm({ onChange, newData }) {
  // 각 input의 참조를 생성
  const idRef = useRef();
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();

  // 유효성 검사 함수
  const validateForm = () => {
    let isValid = true;

    // 각 input의 값이 비어있는지 체크
    if (!idRef.current.value) {
      alert("ID를 입력해주세요.");
      isValid = false;
    }
    if (!nameRef.current.value) {
      alert("이름을 입력해주세요.");
      isValid = false;
    }
    if (!emailRef.current.value) {
      alert("이메일을 입력해주세요.");
      isValid = false;
    }
    if (!phoneRef.current.value) {
      alert("전화번호를 입력해주세요.");
      isValid = false;
    }

    return isValid;
  };

  // 폼 제출 함수
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // 유효성 검사 통과 시 폼 데이터 처리
      console.log("폼 제출됨:", newData);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={onChange}
          ref={idRef} // ref 연결
        />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={onChange}
          ref={nameRef} // ref 연결
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={onChange}
          ref={emailRef} // ref 연결
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={onChange}
          ref={phoneRef} // ref 연결
        />
      </label><br />
      <button type="submit">제출</button>
    </form>
  );
}

export default AddDataForm;
