// AddDataModal.js
import React, { useState } from "react";

const AddDataModal = ({ onAddData, isOpen, onClose }) => {
    const [newData, setNewData] = useState({ id: "", name: "", email: "", phone: "" });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = () => {
        onAddData(newData);
        setNewData({ id: "", name: "", email: "", phone: "" });
    };

    if (!isOpen) return null; // 모달이 열려있을 때만 렌더링

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>데이터 추가</h3>
                <label>ID: <input type="text" name="id" value={newData.id} onChange={handleChange} /></label><br></br>
                <label>이름: <input type="text" name="name" value={newData.name} onChange={handleChange} /></label><br></br>
                <label>Email: <input type="email" name="email" value={newData.email} onChange={handleChange} /></label><br></br>
                <label>Phone: <input type="text" name="phone" value={newData.phone} onChange={handleChange} /></label><br></br>
                <button onClick={handleSubmit}>추가</button>
            </div>
        </div>
    );
};

export default AddDataModal;
