import React, { useState, useRef } from "react";

function AddDataForm({ onChange, newData }) {
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();

  // 데이터 변경 처리 함수 (모든 필드에 대해 공통적으로 처리)
  const handleChange = (e) => {
    const { name, value } = e.target;

    // 전화번호 형식 자동 수정
    if (name === "phone") {
      const formattedPhone = value
        .replace(/[^\d]/g, "") // 숫자만 남기기
        .replace(/(\d{3})(\d{4})(\d{4})/, "$1-$2-$3"); // ***-****-**** 형식으로 변환
      onChange({ ...newData, [name]: formattedPhone });
    } else {
      onChange({ ...newData, [name]: value });
    }
  };

  // 제출 시 유효성 체크
  const handleSubmit = (e) => {
    e.preventDefault();

    // 유효성 체크: 빈 값이 있는지 확인
    if (!newData.name || !newData.email || !newData.phone) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    // 이메일 유효성 체크
    if (!/^[\w-]+(\.[\w-]+)*@([\w-]+\.)+[a-zA-Z]{2,7}$/.test(newData.email)) {
      alert("유효한 이메일 주소를 입력해주세요.");
      emailRef.current.focus();
      return;
    }

    // 전화번호 유효성 체크: ***-****-**** 형식
    if (!/^\d{3}-\d{4}-\d{4}$/.test(newData.phone)) {
      alert("전화번호는 ***-****-**** 형식이어야 합니다.");
      phoneRef.current.focus();
      return;
    }

    // 유효성 통과 후 데이터 추가
    onChange(newData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={handleChange}
        />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={handleChange}
          ref={nameRef}
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={handleChange}
          ref={emailRef}
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={handleChange}
          ref={phoneRef}
        />
      </label><br />
    </form>
  );
}

export default AddDataForm;
