import React, { useState, useRef, useEffect } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  // 각 input에 대한 ref 설정
  const idRef = useRef(null);
  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);

  // 데이터 변경 시 상태 업데이트
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  // 데이터가 모두 입력되었을 때 POST 요청
  useEffect(() => {
    // 유효성 검사
    if (idRef.current.value && nameRef.current.value && emailRef.current.value && phoneRef.current.value) {
      // 유효성 검사 통과 시, 데이터 추가 요청
      const addData = async () => {
        try {
          const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newData),
          });

          if (response.ok) {
            onAddData(); // 데이터 갱신
            alert("데이터가 추가되었습니다.");
          } else {
            alert("데이터 추가 실패");
          }
        } catch (error) {
          console.error("Error adding data:", error);
        }

        onClose(); // 모달 닫기
      };

      addData();
    }
  }, [newData, onAddData, onClose]); // newData가 변경될 때마다 실행

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm
          onChange={handleChange}
          newData={newData}
          idRef={idRef}
          nameRef={nameRef}
          emailRef={emailRef}
          phoneRef={phoneRef}
        />
      </div>
    </div>
  );
}

export default AddDataModal;
