// src/components/AddModal.js
import React, { useState } from 'react';

function AddModal({ isOpen, onClose, onAdd }) {
    const [newData, setNewData] = useState({ id: '', name: '', email: '', phone: '' });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = async () => {
        await onAdd(newData);
        setNewData({ id: '', name: '', email: '', phone: '' });
        onClose();
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>데이터 추가</h3>
                <label>ID: <input type="text" name="id" value={newData.id} onChange={handleInputChange} /></label><br></br>
                <label>이름: <input type="text" name="name" value={newData.name} onChange={handleInputChange} /></label><br></br>
                <label>Email: <input type="email" name="email" value={newData.email} onChange={handleInputChange} /></label><br></br>
                <label>Phone: <input type="text" name="phone" value={newData.phone} onChange={handleInputChange} /></label><br></br>
                <button onClick={handleSubmit}>추가</button>
            </div>
        </div>
    );
}

export default AddModal;
