// src/components/DataList.js
import React, { useEffect, useState } from 'react';
import DataItem from './DataItem';

function DataList({ onEdit }) {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
        const result = await response.json();
        setData(result);
    };

    const deleteData = async (id) => {
        await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, { method: 'DELETE' });
        fetchData();
    };

    return (
        <div id="dataList">
            {data.map((item) => (
                <DataItem key={item.id} item={item} onEdit={onEdit} onDelete={deleteData} />
            ))}
        </div>
    );
}

export default DataList;
