import React, { useState } from "react";
import DataList from "../DataList";
import AddDataModal from "../AddDataModal";
import UpdateDataModal from "../UpdateDataModal";

const ShowList = () => {
    const [data, setData] = useState([]);
    const [isDataLoaded, setIsDataLoaded] = useState(false); // Whether data has been fetched
    const [isAddModalOpen, setIsAddModalOpen] = useState(false); // For Add Modal state
    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false); // For Update Modal state
    const [editData, setEditData] = useState(null); // For storing data to update

    // Fetch data from the server
    const fetchData = async () => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
            const result = await response.json();
            setData(result);
            setIsDataLoaded(true); // Set data as loaded
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    // Add data and refresh list
    const handleAddData = async (newData) => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newData),
            });
            if (response.ok) {
                alert("데이터가 추가되었습니다!");
                fetchData(); // Fetch new data
                setIsAddModalOpen(false); // Close modal after add
            }
        } catch (error) {
            console.error("Error adding data:", error);
        }
    };

    // Update data and refresh list
    const handleUpdateData = async (updatedData) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${updatedData.id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updatedData),
            });
            if (response.ok) {
                alert("데이터가 수정되었습니다!");
                fetchData(); // Fetch updated data
                setIsUpdateModalOpen(false); // Close modal after update
            }
        } catch (error) {
            console.error("Error updating data:", error);
        }
    };

    // Delete data and refresh list
    const handleDeleteData = async (id) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                alert("데이터가 삭제되었습니다!");
                fetchData(); // Fetch updated data
            }
        } catch (error) {
            console.error("Error deleting data:", error);
        }
    };

    // Open update modal with pre-filled data
    const openUpdateModal = (item) => {
        setEditData(item); // Store item to update
        setIsUpdateModalOpen(true); // Open update modal
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
            <button onClick={fetchData}>데이터 조회</button>
            <button onClick={() => setIsAddModalOpen(true)}>데이터 추가</button>

            {/* Add Data Modal */}
            <AddDataModal 
                isOpen={isAddModalOpen} 
                onClose={() => setIsAddModalOpen(false)} 
                onAddData={handleAddData} 
            />

            {/* Update Data Modal */}
            {isUpdateModalOpen && (
                <UpdateDataModal 
                    isOpen={isUpdateModalOpen} 
                    onClose={() => setIsUpdateModalOpen(false)} 
                    onUpdateData={handleUpdateData} 
                    data={editData} 
                />
            )}

            {/* Data List */}
            {isDataLoaded && (
                <DataList 
                    data={data} 
                    onUpdateData={openUpdateModal} 
                    onDeleteData={handleDeleteData} 
                />
            )}
        </div>
    );
};

export default ShowList;
