import React, { useState, useEffect, useRef } from "react";

function UpdateDataForm({ item, onUpdateData }) {
  const [updatedData, setUpdatedData] = useState({
    name: item.name,
    email: item.email,
    phone: item.phone,
  });

  const [modified, setModified] = useState(0);  // 수정 횟수 추가

  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);

  // item이 변경되면 데이터를 업데이트
  useEffect(() => {
    setUpdatedData({
      name: item.name,
      email: item.email,
      phone: item.phone,
    });
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({ ...prev, [name]: value }));

    // 값이 바뀔 때마다 API로 PUT 요청을 보냄
    const updateData = async () => {
      try {
        const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${item.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ ...updatedData, [name]: value }),
        });
        if (response.ok) {
          setModified((prev) => prev + 1);  // 수정 횟수 증가
        } else {
          alert("수정 실패");
        }
      } catch (error) {
        console.error("Error updating data:", error);
      }
    };

    updateData();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // 유효성 검사
    if (!nameRef.current.value) {
      alert("이름을 입력해 주세요.");
      return;
    }
    if (!emailRef.current.value) {
      alert("이메일을 입력해 주세요.");
      return;
    }
    if (!phoneRef.current.value) {
      alert("전화번호를 입력해 주세요.");
      return;
    }

    // 수정된 데이터를 서버에 반영
    onUpdateData();
    alert("데이터가 수정되었습니다.");
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID: <input type="text" name="id" value={item.id} readOnly />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={updatedData.name}
          onChange={handleChange}
          ref={nameRef} // ref 추가
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={updatedData.email}
          onChange={handleChange}
          ref={emailRef} // ref 추가
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={updatedData.phone}
          onChange={handleChange}
          ref={phoneRef} // ref 추가
        />
      </label><br />
      <p>수정 횟수: {modified}</p>
      <button type="submit">수정 완료</button>
    </form>
  );
}

export default UpdateDataForm;
