import React, { useState, useEffect } from "react";
import DataList from "../DataList";
import AddDataModal from "../AddDataModal";
import UpdateDataModal from "../UpdateDataModal";

const ShowList = () => {
    const [data, setData] = useState([]);
    const [refresh, setRefresh] = useState(false);  // 새로고침 상태 관리

    // 서버에서 데이터 가져오기
    useEffect(() => {
        fetchData();
    }, [refresh]);

    const fetchData = async () => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
            const result = await response.json();
            setData(result);
        } catch (error) {
            console.error("데이터 가져오기 오류:", error);
        }
    };

    // 데이터 추가 후 새로고침
    const handleAddData = async (newData) => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newData),
            });
            if (response.ok) {
                alert("데이터가 추가되었습니다!");
                setRefresh(!refresh);  // 새로고침 상태 토글
            }
        } catch (error) {
            console.error("데이터 추가 오류:", error);
        }
    };

    // 데이터 수정 후 새로고침
    const handleUpdateData = async (id, updatedData) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updatedData),
            });
            if (response.ok) {
                alert("데이터가 수정되었습니다!");
                setRefresh(!refresh);  // 새로고침 상태 토글
            }
        } catch (error) {
            console.error("데이터 수정 오류:", error);
        }
    };

    // 데이터 삭제 후 새로고침
    const handleDeleteData = async (id) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                alert("데이터가 삭제되었습니다!");
                setRefresh(!refresh);  // 새로고침 상태 토글
            }
        } catch (error) {
            console.error("데이터 삭제 오류:", error);
        }
    };

    // return (
    //     <div>
    //         <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
    //         <button onClick={() => fetchData()}>데이터 조회</button>
    //         <AddDataModal onAddData={handleAddData} />
    //         <DataList data={data} onUpdateData={handleUpdateData} onDeleteData={handleDeleteData} />
    //     </div>
    // );
    const handleEdit = (data) => {
        setEditData(data);
        openModal('update'); // Show update modal
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
            <button onClick={() => fetchData()}>데이터 조회</button>
            <AddDataModal onAddData={handleAddData} />
            <UpdateDataModal data={editData} onUpdateData={handleUpdateData} />
            <DataList data={data} onEdit={handleEdit} onDelete={handleDeleteData} />
        </div>
    );

};


export default ShowList;
