import React, { useState, useRef } from "react";

function UpdateDataForm({ item, onUpdateData, onClose }) {
  const [updatedData, setUpdatedData] = useState({
    name: item.name,
    email: item.email,
    phone: item.phone,
  });

  const [updateCount, setUpdateCount] = useState(0); // 수정 횟수
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();

  const handleChange = async (e) => {
    const { name, value } = e.target;

    // 값 업데이트 및 수정 횟수 증가
    setUpdatedData((prev) => ({ ...prev, [name]: value }));
    setUpdateCount((prev) => prev + 1);

    // 서버에 즉각 반영
    try {
      const response = await fetch(
        `https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${item.id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ ...updatedData, [name]: value }),
        }
      );
      if (!response.ok) {
        alert("서버 업데이트 실패");
      }
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const validateInput = () => {
    if (!nameRef.current.value.trim()) {
      alert("이름을 입력하세요.");
      return false;
    }
    if (!emailRef.current.value.includes("@")) {
      alert("올바른 이메일 주소를 입력하세요.");
      return false;
    }
    if (!phoneRef.current.value.match(/^\d{3}-\d{4}-\d{4}$/)) {
      alert("전화번호는 ***-****-**** 형식으로 입력해야 합니다.");
      return false;
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateInput()) {
      alert("유효성 체크 통과! 서버로 데이터를 보낼 준비가 되었습니다.");
      onUpdateData(updatedData);
      onClose();
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID: <input type="text" name="id" value={item.id} readOnly />
      </label>
      <br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          ref={nameRef}
          value={updatedData.name}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          ref={emailRef}
          value={updatedData.email}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          ref={phoneRef}
          value={updatedData.phone}
          onChange={handleChange}
        />
      </label>
      <br />
      <p>수정 횟수: {updateCount}</p>
      <button type="submit">완료</button>
    </form>
  );
}

export default UpdateDataForm;
