import React, { useState, useEffect } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  useEffect(() => {
    // 모달이 닫힐 때 데이터를 추가하도록 설정
    if (!newData.id || !newData.name || !newData.email || !newData.phone) {
      return;  // 모든 데이터가 입력되기 전까지는 추가되지 않도록
    }

    const addData = async () => {
      // 중복 ID 확인
      const existingData = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
      const existingUsers = await existingData.json();
      const isExisting = existingUsers.some((user) => user.id === newData.id);

      if (isExisting) {
        alert("이미 존재하는 ID입니다.");
        return;
      }

      // 데이터 추가
      const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      if (response.ok) {
        alert("데이터가 추가되었습니다.");
        onAddData();  // 데이터 갱신
        onClose();  // 모달 닫기
      } else {
        alert("추가 실패");
      }
    };

    // 모달이 닫힐 때 데이터 추가 실행
    return () => {
      if (newData.id && newData.name && newData.email && newData.phone) {
        addData();
      }
    };
  }, [newData, onAddData, onClose]);

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm onChange={handleChange} />
      </div>
    </div>
  );
}

export default AddDataModal;
