import React, { useState, useEffect } from "react";
import DataList from "../DataList";
import AddDataModal from "../AddDataModal";
import UpdateDataModal from "../UpdateDataModal";

const ShowList = () => {
    const [data, setData] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
    const [selectedData, setSelectedData] = useState(null);

    useEffect(() => {
        fetchData();
    }, [refresh]);

    const fetchData = async () => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
            const result = await response.json();
            setData(result);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    const handleAddData = async (newData) => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newData),
            });
            if (response.ok) {
                alert("데이터가 추가되었습니다!");
                setIsAddModalOpen(false);
                setRefresh(!refresh);
            }
        } catch (error) {
            console.error("Error adding data:", error);
        }
    };

    const handleUpdateData = async (id, updatedData) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updatedData),
            });
            if (response.ok) {
                alert("데이터가 수정되었습니다!");
                setIsUpdateModalOpen(false);
                setRefresh(!refresh);
            }
        } catch (error) {
            console.error("Error updating data:", error);
        }
    };

    const handleDeleteData = async (id) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                alert("데이터가 삭제되었습니다!");
                setRefresh(!refresh);
            }
        } catch (error) {
            console.error("Error deleting data:", error);
        }
    };

    const openUpdateModal = (data) => {
        setSelectedData(data);
        setIsUpdateModalOpen(true);
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
            <button onClick={fetchData}>데이터 조회</button>
            <button onClick={() => setIsAddModalOpen(true)}>데이터 추가</button>

            <AddDataModal onAddData={handleAddData} isOpen={isAddModalOpen} onClose={() => setIsAddModalOpen(false)} />

            <UpdateDataModal
                isOpen={isUpdateModalOpen}
                onClose={() => setIsUpdateModalOpen(false)}
                data={selectedData}
                onUpdateData={handleUpdateData}
            />

            {data && (
                <DataList data={data} onUpdateData={openUpdateModal} onDeleteData={handleDeleteData} />
            )}
        </div>
    );
};

export default ShowList;
