// src/components/UpdateModal.js
import React, { useState, useEffect } from 'react';

function UpdateModal({ isOpen, onClose, onUpdate, item }) {
    const [updatedData, setUpdatedData] = useState(item || { name: '', email: '', phone: '' });

    useEffect(() => {
        setUpdatedData(item || { name: '', email: '', phone: '' });
    }, [item]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = async () => {
        await onUpdate(updatedData);
        onClose();
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>데이터 수정</h3>
                <label>이름: <input type="text" name="name" value={updatedData.name} onChange={handleInputChange} /></label>&nbsp
                <label>Email: <input type="email" name="email" value={updatedData.email} onChange={handleInputChange} /></label>
                <label>Phone: <input type="text" name="phone" value={updatedData.phone} onChange={handleInputChange} /></label>
                <button onClick={handleSubmit}>수정</button>
            </div>
        </div>
    );
}

export default UpdateModal;
