import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function Update() {
  const { id } = useParams();
  console.log(id);  // ID 값이 제대로 출력되는지 확인

  const [userData, setUserData] = useState({});
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!id) {
          throw new Error("ID 값이 없습니다.");
        }
        const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`);
        if (!response.ok) {
          throw new Error("사용자 데이터를 불러오는 중 오류가 발생했습니다.");
        }
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        console.error(error);
        setError(error.message);
      }
    };
    fetchData();
  }, [id]);

  return (
    <div>
      <h1>사용자 정보 수정</h1>
      {error && <p>{error}</p>}
      {userData ? (
        <div>
          <p>이름: {userData.name}</p>
          <p>이메일: {userData.email}</p>
          <p>전화번호: {userData.phone}</p>
        </div>
      ) : (
        <p>사용자 데이터를 불러오는 중...</p>
      )}
    </div>
  );
}
