import React, { useState } from "react";

function UpdateDataForm({ item, onUpdateData }) {
  const [updatedData, setUpdatedData] = useState({
    name: item.name,
    email: item.email,
    phone: item.phone,
  });
  const [updateCount, setUpdateCount] = useState(0); // 수정 횟수 상태 추가

  const handleChange = async (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({ ...prev, [name]: value }));
    setUpdateCount((prev) => prev + 1); // 수정 횟수 증가

    try {
      await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${item.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...updatedData, [name]: value }),
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <form>
      <p>총 수정 횟수: {updateCount}</p>
      <label>
        ID: <input type="text" name="id" value={item.id} readOnly />
      </label>
      <br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={updatedData.name}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={updatedData.email}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={updatedData.phone}
          onChange={handleChange}
        />
      </label>
      <br />
    </form>
  );
}

export default UpdateDataForm;
