import React, { useState } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const handleClose = async (newData) => {
    // 데이터가 모두 입력되었을 경우만 POST 요청
    if (!newData.id || !newData.name || !newData.email || !newData.phone) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    // 데이터 추가 요청
    try {
      const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      if (response.ok) {
        onAddData(); // 데이터 갱신
        alert("데이터가 추가되었습니다.");
      } else {
        alert("데이터 추가 실패");
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }

    onClose(); // 모달 닫기
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={() => handleClose()}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm onClose={handleClose} />
      </div>
    </div>
  );
}

export default AddDataModal;
