import React, { useState } from "react";

const baseURL = "https://6728fadd6d5fa4901b6bbadc.mockapi.io/users";

function App() {
  const [data, setData] = useState([]);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [editItem, setEditItem] = useState(null);

  // Fetch data from the API
  const fetchData = async () => {
    try {
      const response = await fetch(baseURL);
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  // Add data to the API
  const handleAddData = async (newData) => {
    try {
      const response = await fetch(baseURL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });
      if (response.ok) {
        alert("데이터가 추가되었습니다.");
        fetchData(); // Refresh the data list
        setIsAddModalOpen(false);
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // Update data in the API
  const handleUpdateData = async (updatedData) => {
    try {
      const response = await fetch(`${baseURL}/${editItem.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });
      if (response.ok) {
        alert("데이터가 수정되었습니다.");
        fetchData(); // Refresh the data list
        setIsUpdateModalOpen(false);
        setEditItem(null);
      }
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  // Delete data from the API
  const handleDeleteData = async (id) => {
    try {
      const response = await fetch(`${baseURL}/${id}`, { method: "DELETE" });
      if (response.ok) {
        alert("데이터가 삭제되었습니다.");
        fetchData(); // Refresh the data list
      }
    } catch (error) {
      console.error("Error deleting data:", error);
    }
  };

  // Open Modal for Add Data
  const openAddModal = () => {
    setIsAddModalOpen(true);
  };

  // Open Modal for Update Data
  const openUpdateModal = (item) => {
    setEditItem(item);
    setIsUpdateModalOpen(true);
  };

  // Close Modal
  const closeModal = () => {
    setIsAddModalOpen(false);
    setIsUpdateModalOpen(false);
    setEditItem(null);
  };

  return (
    <div className="App">
      <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
      <button onClick={fetchData}>데이터 조회</button>
      <button onClick={openAddModal}>데이터 추가</button>

      {/* Data List */}
      <div id="dataList">
        {data.length > 0 ? (
          data.map((item) => (
            <div key={item.id} className="data-item">
              <div className="data-field">ID: {item.id}</div>
              <div className="data-field">이름: {item.name}</div>
              <div className="data-field">Email: {item.email}</div>
              <div className="data-field">Phone: {item.phone}</div>
              <div className="button-group">
                <button onClick={() => openUpdateModal(item)}>수정</button>
                <button onClick={() => handleDeleteData(item.id)}>삭제</button>
              </div>
            </div>
          ))
        ) : (
          <p>데이터가 없습니다. '데이터 조회' 버튼을 눌러주세요.</p>
        )}
      </div>

      {/* Add Data Modal */}
      {isAddModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h3>데이터 추가</h3>
            <AddDataForm onAddData={handleAddData} />
          </div>
        </div>
      )}

      {/* Update Data Modal */}
      {isUpdateModalOpen && editItem && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h3>데이터 수정</h3>
            <UpdateDataForm
              item={editItem}
              onUpdateData={handleUpdateData}
            />
          </div>
        </div>
      )}
    </div>
  );
}

// Add Data Form Component
function AddDataForm({ onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddData(newData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={handleChange}
        />
      </label><br></br>
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={handleChange}
        />
      </label><br></br>
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={handleChange}
        />
      </label><br></br>
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={handleChange}
        />
      </label><br></br>
      <button type="submit">추가</button>
    </form>
  );
}

// Update Data Form Component
function UpdateDataForm({ item, onUpdateData }) {
  const [updatedData, setUpdatedData] = useState({
    name: item.name,
    email: item.email,
    phone: item.phone,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdateData(updatedData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID: <input type="text" name="id" value={item.id} readOnly />
      </label><br></br>
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={updatedData.name}
          onChange={handleChange}
        />
      </label><br></br>
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={updatedData.email}
          onChange={handleChange}
        />
      </label><br></br>
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={updatedData.phone}
          onChange={handleChange}
        />
      </label><br></br>
      <button type="submit">수정</button>
    </form>
  );
}

export default App;
