import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function Update() {
  const { id } = useParams(); // URL에서 id 추출
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    phone: ""
  }); // 사용자 데이터 상태
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // useNavigate로 변경

  useEffect(() => {
    // 사용자의 데이터를 불러오기
    const fetchData = async () => {
      try {
        const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`);
        if (!response.ok) {
          throw new Error("사용자 데이터를 불러오는 중 오류가 발생했습니다.");
        }
        const data = await response.json();
        setUserData({
          name: data.name,
          email: data.email,
          phone: data.phone
        });
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(userData) // 수정된 사용자 데이터 보내기
      });

      if (!response.ok) {
        throw new Error("데이터를 업데이트하는 중 오류가 발생했습니다.");
      }

      navigate(`/detail/${id}`); // 업데이트 후 상세 페이지로 이동
    } catch (error) {
      setError(error.message);
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>사용자 정보 수정</h2>
      <form onSubmit={handleSubmit}>
        <label>
          아이디:
          <input type="text" name="id" value={id} disabled />
        </label>
        <br />
        <label>
          이름:
          <input
            type="text"
            name="name"
            value={userData.name}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          이메일:
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          전화번호:
          <input
            type="text"
            name="phone"
            value={userData.phone}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="submit">저장</button>
      </form>
    </div>
  );
}
