import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function Detail() {
  const { id } = useParams();  // URL에서 id 추출
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // id를 문자열로 사용하도록 수정
        const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`);
        if (!response.ok) {
          throw new Error('"Router를 사용하여 페이지 이동을 구현하였습니다."');
        }
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [id]);  // id가 변경될 때마다 fetchData가 실행되도록 함

  if (error) {
    return <div>{error}</div>;
  }

  if (!userData) {
    return <div>데이터를 불러오는 중...</div>;
  }

  return (
    <div>
      <h2>상세 정보</h2>
      <p>이름: {userData.name}</p>
      <p>사용자명: {userData.username}</p>
      <p>이메일: {userData.email}</p>
      <p>전화번호: {userData.phone}</p>
      <p>웹사이트: <a href={userData.website} target="_blank" rel="noopener noreferrer">{userData.website}</a></p>
      <p>주소: {userData.province} {userData.city} {userData.district} {userData.street}</p>
      <p>우편번호: {userData.zipcode}</p>
    </div>
  );
}
