import React, { useRef } from "react";

function UpdateDataForm({ item, onUpdateData, onClose }) {
  const nameRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedData = { ...item, name: nameRef.current.value };
    await fetch(`https://api.example.com/data/${item.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedData),
    });

    onUpdateData();
    onClose();
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        이름:
        <input ref={nameRef} defaultValue={item.name} type="text" required />
      </label>
      <button type="submit">수정</button>
      <button type="button" onClick={onClose}>
        취소
      </button>
    </form>
  );
}

export default UpdateDataForm;
