import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function Detail() {
  const { id } = useParams(); // URL에서 ID를 가져옴
  const navigate = useNavigate(); // 다른 페이지로 이동할 때 사용
  const [data, setData] = useState(null); // 상세 데이터 상태

  useEffect(() => {
    // 특정 ID에 대한 데이터 가져오기
    const fetchData = async () => {
      try {
        const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`);
        if (!response.ok) throw new Error("데이터를 가져오지 못했습니다.");
        const result = await response.json();
        setData(result); // 데이터 상태 업데이트
      } catch (error) {
        console.error(error);
        alert("데이터를 불러오는 중 오류가 발생했습니다.");
      }
    };

    fetchData();
  }, [id]);

  // 데이터가 없을 경우 로딩 중 메시지 표시
  if (!data) {
    return <div>로딩 중...</div>;
  }

  return (
    <div>
      <h2>데이터 상세 정보</h2>
      <p>
        <strong>ID:</strong> {data.id}
      </p>
      <p>
        <strong>이름:</strong> {data.name}
      </p>
      <p>
        <strong>이메일:</strong> {data.email}
      </p>
      <p>
        <strong>전화번호:</strong> {data.phone}
      </p>
      <button onClick={() => navigate("/list")}>목록으로</button>
    </div>
  );
}
