import React, { useState, useEffect } from "react";

const UpdateDataModal = ({ isOpen, onClose, onUpdateData, data }) => {
    const [updatedData, setUpdatedData] = useState(data);

    useEffect(() => {
        setUpdatedData(data); // Sync updated data when modal opens
    }, [data]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = () => {
        onUpdateData(updatedData); // Pass the updated data to the parent
    };

    if (!isOpen) return null; // Don't render modal if not open

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>데이터 수정</h3>
                <label>ID: <input type="text" name="id" value={updatedData.id} disabled /></label>
                <label>Name: <input type="text" name="name" value={updatedData.name} onChange={handleChange} /></label>
                <label>Email: <input type="email" name="email" value={updatedData.email} onChange={handleChange} /></label>
                <label>Phone: <input type="text" name="phone" value={updatedData.phone} onChange={handleChange} /></label>
                <button onClick={handleSubmit}>수정</button>
            </div>
        </div>
    );
};

export default UpdateDataModal;
