import React, { useState, useEffect } from "react";
import UpdateDataForm from "./UpdateDataForm";

function UpdateDataModal({ item, onClose, onUpdateData }) {
  const [modified, setModified] = useState(0);  // 수정 횟수

  const handleUpdateData = (updatedData) => {
    onUpdateData(updatedData); // 데이터 업데이트
    setModified(modified + 1);  // 수정 횟수 증가
  };

  const handleClose = () => {
    onUpdateData(); // 수정된 데이터 반영 후, 데이터를 다시 불러오기
    onClose(); // 모달 닫기
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={handleClose}>
          &times;
        </span>
        <h3>데이터 수정</h3>
        <UpdateDataForm item={item} onUpdateData={handleUpdateData} />
        <p>수정 횟수: {modified}</p>
      </div>
    </div>
  );
}

export default UpdateDataModal;
