import React, { useState } from "react";

function AddDataForm({ onClose }) {
  const [newData, setNewData] = useState({
    id: "",   // ID도 초기화
    name: "",
    email: "",
    phone: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <form>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={handleChange}
        />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={handleChange}
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={handleChange}
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={handleChange}
        />
      </label><br />
    </form>
  );
}

export default AddDataForm;
