import React, { useState, useRef } from "react";

function AddDataForm({ onAddData, onClose }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  const idRef = useRef();
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  const validateInput = () => {
    if (!idRef.current.value.trim()) {
      alert("ID를 입력하세요.");
      return false;
    }
    if (!nameRef.current.value.trim()) {
      alert("이름을 입력하세요.");
      return false;
    }
    if (!emailRef.current.value.includes("@")) {
      alert("올바른 이메일 주소를 입력하세요.");
      return false;
    }
    if (!phoneRef.current.value.match(/^\d{3}-\d{4}-\d{4}$/)) {
      alert("전화번호는 ***-****-**** 형식으로 입력해야 합니다.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateInput()) {
      return;
    }

    try {
      const response = await fetch(
        "https://6728fadd6d5fa4901b6bbadc.mockapi.io/users",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newData),
        }
      );
      if (response.ok) {
        alert("데이터가 추가되었습니다.");
        onAddData(); // Refresh data after adding
        onClose(); // Close the modal
      } else {
        alert("추가 실패");
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          ref={idRef}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          ref={nameRef}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          ref={emailRef}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          ref={phoneRef}
          onChange={handleChange}
        />
      </label>
      <br />
      <button type="submit">추가</button>
    </form>
  );
}

export default AddDataForm;
