import React, { useRef } from "react";

function AddDataForm({ onChange, newData }) {
  const idRef = useRef();
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();

  const validateForm = () => {
    if (!newData.id) {
      idRef.current.focus();
      alert("ID를 입력해주세요.");
      return false;
    }
    if (!newData.name) {
      nameRef.current.focus();
      alert("이름을 입력해주세요.");
      return false;
    }
    if (!newData.email) {
      emailRef.current.focus();
      alert("이메일을 입력해주세요.");
      return false;
    }
    if (!newData.phone) {
      phoneRef.current.focus();
      alert("전화번호를 입력해주세요.");
      return false;
    }
    return true;
  };

  return (
    <form>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={onChange}
          ref={idRef}
        />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={onChange}
          ref={nameRef}
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={onChange}
          ref={emailRef}
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={onChange}
          ref={phoneRef}
        />
      </label><br />
    </form>
  );
}

export default AddDataForm;
