import React, { useState, useEffect } from "react";
import AddDataForm from "./AddDataForm";

function AddDataModal({ onClose, onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  const [isFormValid, setIsFormValid] = useState(false);  // 유효성 검사 상태

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // 모달이 닫힐 때 데이터 추가
  const handleClose = async () => {
    // 데이터가 유효한지 확인
    if (!newData.id || !newData.name || !newData.email || !newData.phone) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    // 중복 ID 확인
    const existingData = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
    const existingUsers = await existingData.json();
    const isExisting = existingUsers.some((user) => user.id === newData.id);

    if (isExisting) {
      alert("이미 존재하는 ID입니다.");
      return;
    }

    // 데이터 추가
    const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newData),
    });

    if (response.ok) {
      alert("데이터가 추가되었습니다.");
      onAddData();  // 데이터 갱신
      onClose();  // 모달 닫기
    } else {
      alert("추가 실패");
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h3>데이터 추가</h3>
        <AddDataForm onChange={handleChange} />

        {/* 모달 닫을 때 데이터 추가 */}
        <button onClick={handleClose}>모달 닫고 추가하기</button>
      </div>
    </div>
  );
}

export default AddDataModal;
