import React, { useState } from "react";

function AddDataForm({ onAddData }) {
  const [newData, setNewData] = useState({
    id: "",
    name: "",
    email: "",
    phone: "",
  });

  const [modified, setModified] = useState(0);  // 수정 횟수

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAdd = async () => {
    // 입력값이 비어있지 않도록 체크
    if (!newData.id || !newData.name || !newData.email || !newData.phone) {
      alert("모든 필드를 채워주세요.");
      return;
    }

    try {
      const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      if (response.ok) {
        onAddData(); // 데이터 갱신
        alert("데이터가 추가되었습니다.");
        setModified(modified + 1);  // 수정 횟수 증가
        setNewData({
          id: "",
          name: "",
          email: "",
          phone: "",
        });  // 입력창 초기화
      } else {
        alert("데이터 추가 실패");
      }
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <form>
      <label>
        ID:
        <input
          type="text"
          name="id"
          value={newData.id}
          onChange={handleChange}
        />
      </label><br />
      <label>
        이름:
        <input
          type="text"
          name="name"
          value={newData.name}
          onChange={handleChange}
        />
      </label><br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={newData.email}
          onChange={handleChange}
        />
      </label><br />
      <label>
        Phone:
        <input
          type="text"
          name="phone"
          value={newData.phone}
          onChange={handleChange}
        />
      </label><br />
      <p>수정 횟수: {modified}</p>
      <button type="button" onClick={handleAdd}>
        데이터 추가
      </button>
    </form>
  );
}

export default AddDataForm;
