import React, { useState } from "react";
import DataList from "../DataList";
import AddDataModal from "../AddDataModal";
import UpdateDataModal from "../UpdateDataModal"; // UpdateDataModal 추가

const ShowList = () => {
    const [data, setData] = useState([]);
    const [isDataLoaded, setIsDataLoaded] = useState(false);
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false); // 수정 모달 상태
    const [currentItem, setCurrentItem] = useState(null); // 수정할 데이터

    // Fetch data from the server
    const fetchData = async () => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users");
            const result = await response.json();
            setData(result);
            setIsDataLoaded(true);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    // Add data and refresh list
    const handleAddData = async (newData) => {
        try {
            const response = await fetch("https://6728fadd6d5fa4901b6bbadc.mockapi.io/users", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newData),
            });
            if (response.ok) {
                alert("데이터가 추가되었습니다!");
                fetchData(); 
                setIsAddModalOpen(false); 
            }
        } catch (error) {
            console.error("Error adding data:", error);
        }
    };

    // Update data and refresh list
    const handleUpdateData = async (id, updatedData) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updatedData),
            });
            if (response.ok) {
                alert("데이터가 수정되었습니다!");
                fetchData(); 
                setIsUpdateModalOpen(false); // 수정 후 모달 닫기
            }
        } catch (error) {
            console.error("Error updating data:", error);
        }
    };

    // Delete data and refresh list
    const handleDeleteData = async (id) => {
        try {
            const response = await fetch(`https://6728fadd6d5fa4901b6bbadc.mockapi.io/users/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                alert("데이터가 삭제되었습니다!");
                fetchData(); 
            }
        } catch (error) {
            console.error("Error deleting data:", error);
        }
    };

    // Handle edit button click
    const handleEditClick = (item) => {
        setCurrentItem(item); // 수정할 아이템 저장
        setIsUpdateModalOpen(true); // 수정 모달 열기
    };

    return (
        <div>
            <h1>AJAX CRUD 페이지 구현 (22200150 김유진)</h1>
            <button onClick={fetchData}>데이터 조회</button>
            <button onClick={() => setIsAddModalOpen(true)}>데이터 추가</button>

            <AddDataModal onAddData={handleAddData} />
            {isUpdateModalOpen && (
                <UpdateDataModal
                    currentItem={currentItem}
                    onUpdateData={handleUpdateData}
                    onClose={() => setIsUpdateModalOpen(false)}
                />
            )}

            {isDataLoaded && (
                <DataList 
                    data={data} 
                    onUpdateData={handleUpdateData} 
                    onDeleteData={handleDeleteData} 
                    onEdit={handleEditClick} // 수정 버튼 처리
                />
            )}
        </div>
    );
};

export default ShowList;
